    private void adicionarNumero(int numeroInserido){
        int numAtual = Integer.parseInt(jLResultado.getText());
                
        numAtual = (numAtual * 10) + numeroInserido;
        
        jLResultado.setText(Integer.toString(numAtual));
    }
